contracts/AMMFactory.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.5;

import "./interfaces/IAMMFactory.sol";
import "./AMMTokenPair.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";

//AMM - 
contract AMMFactory is
    IAMMFactory,
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable
{
    address public feeTo; //
    address public feeToSetter; //
    //,=>(=>)
    mapping(address => mapping(address => address)) public getPair;
    //
    address[] public allPairs;
    //Bytecodehash - 
    bytes32 public constant INIT_CODE_PAIR_HASH =
        0x96e8ac4277198ff8b6f785478aa9a39f403cb768dd02cbee326c3e7da348845f;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev 
     * @param _feeToSetter 
     */
    function initialize(address _feeToSetter) external initializer {
        __Ownable_init(msg.sender);
        __ReentrancyGuard_init();
        feeToSetter = _feeToSetter;
    }

    /**
     * @dev 
     */
    function allPairsLength() external view returns (uint) {
        return allPairs.length;
    }

    /**
     *
     * @param tokenA TokenA
     * @param tokenB TokenB
     * @return pair 
     * @dev 
     */
    function createPair(
        address tokenA,
        address tokenB
    ) external nonReentrant returns (address pair) {
        //tokenAtokenB
        require(tokenA != tokenB, "AMMFactory: IDENTICAL_ADDRESSES");
        //tokenAtokenB,tokenAtokenB
        (address token0, address token1) = tokenA < tokenB
            ? (tokenA, tokenB)
            : (tokenB, tokenA);
        //token00
        require(token0 != address(0), "AMMFactory: ZERO_ADDRESS");
        //token0=>token1
        require(
            getPair[token0][token1] == address(0),
            "AMMFactory: PAIR_EXISTS"
        ); // single check is sufficient
        //bytecode"pair"
        bytes memory bytecode = type(AMMTokenPair).creationCode;
        //token0token1
        bytes32 salt = keccak256(abi.encodePacked(token0, token1));
        //
        assembly {
            //create2,,pair
            pair := create2(0, add(bytecode, 32), mload(bytecode), salt)
        }
        //pair"initialize",token0,token1
        IAMMTokenPair(pair).initialize(token0, token1);
        //token0=>token1=pair
        getPair[token0][token1] = pair;
        //token1=>token0=pair
        getPair[token1][token0] = pair; // populate mapping in the reverse direction
        //pair
        allPairs.push(pair);
        //
        emit PairCreated(token0, token1, pair, allPairs.length);
    }

    /**
     * @dev 
     * @param _feeTo 
     */
    function setFeeTo(address _feeTo) external {
        require(msg.sender == feeToSetter, "AMMFactory: FORBIDDEN");
        feeTo = _feeTo;
    }

    /**
     * @dev 
     * @param _feeToSetter 
     */
    function setFeeToSetter(address _feeToSetter) external {
        require(msg.sender == feeToSetter, "AMMFactory: FORBIDDEN");
        feeToSetter = _feeToSetter;
    }
}

contracts/AMMTokenPair.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.5;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20PermitUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "./interfaces/IAMMTokenPair.sol";
import "./libraries/Math.sol";
import "./libraries/UQ112x112.sol";
import "./interfaces/IAMMFactory.sol";
import "./interfaces/IAMMCallee.sol";

//AMM - 
contract AMMTokenPair is
    Initializable,
    ERC20PermitUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    OwnableUpgradeable,
    IAMMTokenPair
{
    using SafeERC20 for IERC20;
    using UQ112x112 for uint224;

    // = 1000
    uint256 public constant MINIMUM_LIQUIDITY = 10 ** 3;
    //SELECTOR'transfer(address,uint256)'416
    bytes4 private constant SELECTOR =
        bytes4(keccak256(bytes("transfer(address,uint256)")));

    address public factory; //
    address public token0; //token0/
    address public token1; //token1

    uint112 private reserve0; // 0
    uint112 private reserve1; // 1
    uint32 private blockTimestampLast; // 
    //0
    uint256 public price0CumulativeLast;
    //1
    uint256 public price1CumulativeLast;

    //K
    //0*1
    uint256 public kLast;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @param _token0 token0
     * @param _token1 token1
     * @dev ,
     */
    function initialize(address _token0, address _token1) external initializer {
        __ERC20_init("AMM", "AMM");
        __ReentrancyGuard_init();
        __Pausable_init();
        __Ownable_init(msg.sender);

        //
        factory = msg.sender;
        token0 = _token0;
        token1 = _token1;
    }

    /**
     * @dev  - 
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev  - 
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @return _reserve0 0
     * @return _reserve1 1
     * @return _blockTimestampLast 
     * @dev 
     */
    function getReserves()
        public
        view
        returns (
            uint112 _reserve0,
            uint112 _reserve1,
            uint32 _blockTimestampLast
        )
    {
        _reserve0 = reserve0;
        _reserve1 = reserve1;
        _blockTimestampLast = blockTimestampLast;
    }

    /**
     * @param token token
     * @param to    to
     * @param value 
     * @dev 
     */
    function _safeTransfer(address token, address to, uint256 value) private {
        //tokentransfer
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(SELECTOR, to, value)
        );
        //truedata0true
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "AMMTokenPair: TRANSFER_FAILED"
        );
    }

    /**
     * @param balance0 0
     * @param balance1  1
     * @param _reserve0 0
     * @param _reserve1 1
     * @dev 
     */
    function _update(
        uint256 balance0,
        uint256 balance1,
        uint112 _reserve0,
        uint112 _reserve1
    ) private {
        //01uint112
        require(
            balance0 <= type(uint112).max && balance1 <= type(uint112).max,
            "AMMTokenPair: OVERFLOW"
        );
        //,uint32
        uint32 blockTimestamp = uint32(block.timestamp % 2 ** 32);
        //
        unchecked {
            uint32 timeElapsed = blockTimestamp - blockTimestampLast; // overflow is desired
            //>0  0,10
            if (timeElapsed > 0 && _reserve0 != 0 && _reserve1 != 0) {
                // * never overflows, and + overflow is desired
                //0 += 1 * 2**112 / 0 * 
                price0CumulativeLast +=
                    uint256(UQ112x112.encode(_reserve1).uqdiv(_reserve0)) *
                    timeElapsed;
                //1 += 0 * 2**112 / 1 * 
                price1CumulativeLast +=
                    uint256(UQ112x112.encode(_reserve0).uqdiv(_reserve1)) *
                    timeElapsed;
            }
        }
        //0,10,1
        reserve0 = uint112(balance0);
        reserve1 = uint112(balance1);
        //
        blockTimestampLast = blockTimestamp;
        //
        emit Sync(reserve0, reserve1);
    }

    /**
     * @param _reserve0 0
     * @param _reserve1 1
     * @return feeOn
     * @dev 1/6sqrtk
     */
    function _mintFee(
        uint112 _reserve0,
        uint112 _reserve1
    ) private returns (bool feeOn) {
        //feeTo
        address feeTo = IAMMFactory(factory).feeTo();
        //feeTo0,feeOntruefalse
        feeOn = feeTo != address(0);
        //k
        uint256 _kLast = kLast; // gas savings
        //feeOntrue
        if (feeOn) {
            //k0
            if (_kLast != 0) {
                //(_reserve0*_reserve1)
                uint256 rootK = Math.sqrt(uint256(_reserve0) * _reserve1);
                //k
                uint256 rootKLast = Math.sqrt(_kLast);
                //rootK>rootKLast
                if (rootK > rootKLast) {
                    // = erc20 * (rootK - rootKLast)
                    uint256 numerator = totalSupply() * (rootK - rootKLast);
                    // = rootK * 5 + rootKLast
                    uint256 denominator = rootK * 5 + rootKLast;
                    // =  / 
                    uint256 liquidity = numerator / denominator;
                    //  > 0 feeTo
                    if (liquidity > 0) _mint(feeTo, liquidity);
                }
            }
            //_kLast0
        } else if (_kLast != 0) {
            //k=0
            kLast = 0;
        }
    }

    /**
     * @param to to
     * @return liquidity 
     * @dev 
     * @notice 
     */
    function mint(
        address to
    ) external nonReentrant whenNotPaused returns (uint256 liquidity) {
        //`0`,`1`
        (uint112 _reserve0, uint112 _reserve1, ) = getReserves(); // gas savings
        //token0
        uint256 balance0 = IERC20(token0).balanceOf(address(this));
        //token1
        uint256 balance1 = IERC20(token1).balanceOf(address(this));
        //amount0 = 0 - 0
        uint256 amount0 = balance0 - _reserve0;
        //amount1 = 1 - 1
        uint256 amount1 = balance1 - _reserve1;

        //
        bool feeOn = _mintFee(_reserve0, _reserve1);
        //totalSupply,totalSupplymintFee
        uint256 _totalSupply = totalSupply(); // gas savings, must be defined here since totalSupply can update in _mintFee
        //_totalSupply0
        if (_totalSupply == 0) {
            // = (0 * 1) - 1000
            liquidity = Math.sqrt(amount0 * amount1) - MINIMUM_LIQUIDITY;
            //0,
            _mint(address(0), MINIMUM_LIQUIDITY); // permanently lock the first MINIMUM_LIQUIDITY tokens
        } else {
            // =  (amount0 * _totalSupply / _reserve0)  (amount1 * _totalSupply / _reserve1)
            liquidity = Math.min(
                (amount0 * _totalSupply) / _reserve0,
                (amount1 * _totalSupply) / _reserve1
            );
        }
        // > 0
        require(liquidity > 0, "AMMTokenPair: INSUFFICIENT_LIQUIDITY_MINTED");
        //to
        _mint(to, liquidity);

        //
        _update(balance0, balance1, _reserve0, _reserve1);
        //true, k = 0 * 1
        if (feeOn) kLast = uint256(reserve0) * reserve1; // reserve0 and reserve1 are up-to-date
        //
        emit Mint(msg.sender, amount0, amount1);
    }

    /**
     * @param to to
     * @return amount0
     * @return amount1
     * @dev 
     * @notice 
     */
    function burn(
        address to
    )
        external
        nonReentrant
        whenNotPaused
        returns (uint256 amount0, uint256 amount1)
    {
        //`0`,`1`
        (uint112 _reserve0, uint112 _reserve1, ) = getReserves(); // gas savings
        //
        address _token0 = token0; // gas savings
        address _token1 = token1; // gas savings
        //token0
        uint256 balance0 = IERC20(_token0).balanceOf(address(this));
        //token1
        uint256 balance1 = IERC20(_token1).balanceOf(address(this));
        //balanceOf
        uint256 liquidity = balanceOf(address(this));

        //
        bool feeOn = _mintFee(_reserve0, _reserve1);
        //totalSupply,totalSupplymintFee
        uint256 _totalSupply = totalSupply(); // gas savings, must be defined here since totalSupply can update in _mintFee
        //amount0 =  * 0 / totalSupply   
        amount0 = (liquidity * balance0) / _totalSupply; // using balances ensures pro-rata distribution
        //amount1 =  * 1 / totalSupply   
        amount1 = (liquidity * balance1) / _totalSupply; // using balances ensures pro-rata distribution
        //amount0amount10
        require(
            amount0 > 0 && amount1 > 0,
            "AMMTokenPair: INSUFFICIENT_LIQUIDITY_BURNED"
        );
        //
        _burn(address(this), liquidity);
        //amount0_token0to
        _safeTransfer(_token0, to, amount0);
        //amount1_token1to
        _safeTransfer(_token1, to, amount1);
        //balance0
        balance0 = IERC20(_token0).balanceOf(address(this));
        //balance1
        balance1 = IERC20(_token1).balanceOf(address(this));

        //
        _update(balance0, balance1, _reserve0, _reserve1);
        //true, k = 0 * 1
        if (feeOn) kLast = uint256(reserve0) * reserve1; // reserve0 and reserve1 are up-to-date
        //
        emit Burn(msg.sender, amount0, amount1, to);
    }

    /**
     * @param amount0Out 0
     * @param amount1Out 1
     * @param to    to
     * @param data  
     * @dev 
     * @notice 
     */
    function swap(
        uint256 amount0Out,
        uint256 amount1Out,
        address to,
        bytes calldata data
    ) external nonReentrant whenNotPaused {
        //amount0Outamount1Out0
        require(
            amount0Out > 0 || amount1Out > 0,
            "AMMTokenPair: INSUFFICIENT_OUTPUT_AMOUNT"
        );
        //`0`,`1`
        (uint112 _reserve0, uint112 _reserve1, ) = getReserves(); // gas savings
        //`0,1` < `0,1`
        require(
            amount0Out < _reserve0 && amount1Out < _reserve1,
            "AMMTokenPair: INSUFFICIENT_LIQUIDITY"
        );

        //
        uint256 balance0;
        uint256 balance1;
        {
            //_token{0,1}
            // scope for _token{0,1}, avoids stack too deep errors
            address _token0 = token0;
            address _token1 = token1;

            //to_token0_token1
            require(to != _token0 && to != _token1, "AMMTokenPair: INVALID_TO");
            //`0` > 0 `0`token0to
            if (amount0Out > 0) _safeTransfer(_token0, to, amount0Out); // optimistically transfer tokens
            //`1` > 0 `1`token1to
            if (amount1Out > 0) _safeTransfer(_token1, to, amount1Out); // optimistically transfer tokens
            //data0 to
            if (data.length > 0)
                IAMMCallee(to).ammCall(
                    msg.sender,
                    amount0Out,
                    amount1Out,
                    data
                );
            //`0,1` = `token0,1`
            balance0 = IERC20(_token0).balanceOf(address(this));
            balance1 = IERC20(_token1).balanceOf(address(this));
        }
        // 0 > 0 - amount0Out  amount0In = 0 - (0 - amount0Out)  amount0In = 0
        uint256 amount0In = balance0 > _reserve0 - amount0Out
            ? balance0 - (_reserve0 - amount0Out)
            : 0;
        // 1 > 1 - amount1Out  amount1In = 1 - (1 - amount1Out)  amount1In = 0
        uint256 amount1In = balance1 > _reserve1 - amount1Out
            ? balance1 - (_reserve1 - amount1Out)
            : 0;
        //`0||1`0
        require(
            amount0In > 0 || amount1In > 0,
            "AMMTokenPair: INSUFFICIENT_INPUT_AMOUNT"
        );
        {
            //reserve{0,1}
            // scope for reserve{0,1}Adjusted, avoids stack too deep errors
            //0 = 0 * 1000 - (amount0In * 3)
            uint256 balance0Adjusted = balance0 * 1000 - (amount0In * 3);
            //1 = 1 * 1000 - (amount1In * 3)
            uint256 balance1Adjusted = balance1 * 1000 - (amount1In * 3);
            //balance0Adjusted * balance1Adjusted >= 0 * 1 * 1000000
            require(
                balance0Adjusted * balance1Adjusted >=
                    uint256(_reserve0) * _reserve1 * (1000 ** 2),
                "AMMTokenPair: K"
            );
        }

        //
        _update(balance0, balance1, _reserve0, _reserve1);
        //
        emit Swap(msg.sender, amount0In, amount1In, amount0Out, amount1Out, to);
    }

    /**
     * @param to to
     * @dev 
     */
    function skim(address to) external nonReentrant {
        address _token0 = token0; // gas savings
        address _token1 = token1; // gas savings
        //`token0,1`-`0,1`to
        _safeTransfer(
            _token0,
            to,
            IERC20(_token0).balanceOf(address(this)) - reserve0
        );
        _safeTransfer(
            _token1,
            to,
            IERC20(_token1).balanceOf(address(this)) - reserve1
        );
    }

    /**
     * @dev 
     */
    function sync() external nonReentrant {
        _update(
            IERC20(token0).balanceOf(address(this)),
            IERC20(token1).balanceOf(address(this)),
            reserve0,
            reserve1
        );
    }
}

